import React, { useState } from 'react';
import './Agenda.css';
import { FaCalendarAlt, FaEllipsisH, FaExpandArrowsAlt, FaList } from 'react-icons/fa';

const Agenda = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showMoreSettings, setShowMoreSettings] = useState(false);  // More settings menüsü için state ekliyoruz

  const handlePrevDay = () => {
    setCurrentDate(new Date(currentDate.setDate(currentDate.getDate() - 1)));
  };

  const handleNextDay = () => {
    setCurrentDate(new Date(currentDate.setDate(currentDate.getDate() + 1)));
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' });
  };

  const toggleMoreSettings = () => {
    setShowMoreSettings(!showMoreSettings);  // More settings menüsünü açıp kapat
  };

  return (
    <div className="agenda-container">
      <div className="agenda-header">
        <h2>Agenda</h2>
        <div className="agenda-controls">
          <button className="icon-btn" title="Open calendar in profile">
            <FaList />
          </button>
          <button className="icon-btn" title="View in full screen">
            <FaExpandArrowsAlt />
          </button>
          <button className="icon-btn" title="More settings" onClick={toggleMoreSettings}>
            <FaEllipsisH />
          </button>
        </div>
        {showMoreSettings && (
          <div className="more-settings-dropdown">
            <button>Agenda</button>
            <button>Calendar</button>
            <button>Remove card</button>
          </div>
        )}
      </div>
      <div className="agenda-navigation">
        <button onClick={handlePrevDay}>&lt;</button>
        <span>{formatDate(currentDate)}</span>
        <button onClick={handleNextDay}>&gt;</button>
      </div>
      <div className="agenda-options">
        <span className="today-text">Today</span>
        <div className="dropdown">
          <button className="calendar-btn" title="Open calendar settings">
            <FaCalendarAlt />
          </button>
          <div className="dropdown-content">
            <button>Add Calendar</button>
            <label>
              <input type="checkbox" />
              Hide reminders
            </label>
          </div>
        </div>
      </div>
      <div className="agenda-content">
        <p>Agenda items from your calendar will show here.</p>
        <button className="btn">+ Add calendar integrations</button>
      </div>
    </div>
  );
};

export default Agenda;
