import React, { useState, useRef, useEffect } from 'react';

const TaskList = ({ toggleDropdown: externalToggle, isDropdownOpen: externalDropdownOpen, dropdownRef: externalDropdownRef }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(externalDropdownOpen || false); // Menü açma/kapatma durumu
  const dropdownRef = externalDropdownRef || useRef(null); // Eğer dışarıdan ref gelmezse burada oluştur

  // Dropdown menüyü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation();
    if (externalToggle) {
      externalToggle(e); // Eğer dışarıdan bir toggle fonksiyonu geldiyse onu kullan
    } else {
      setIsDropdownOpen(!isDropdownOpen); // Yoksa bu bileşende yönet
    }
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    // Pencereye tıklama olayını ekle
    window.addEventListener('click', handleClickOutside);

    // Bileşen temizlendiğinde olay dinleyicisini kaldır
    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div>
      <h2>Task List</h2>
      {/* List Options butonu sadece TaskList içinde olacak */}
      <button className="list-options-btn" onClick={toggleDropdown}>
        List Options
      </button>

      {/* List Options menüsü */}
      {(externalDropdownOpen !== undefined ? externalDropdownOpen : isDropdownOpen) && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <button>Rename</button>
          <button>Copy link to view</button>
          <button>Add to favorites</button>
          <button>Customize view</button>
          <button>Pin view</button>
          <button>Private view</button>
        </div>
      )}

      <p>No tasks available.</p>
    </div>
  );
};

export default TaskList;
