import React, { useState } from 'react';
import Home from './components/Home'; // Home bileşenini içe aktar
import './App.css';
import { FaHome, FaInbox } from 'react-icons/fa';

const App = () => {
  const [activeTab, setActiveTab] = useState('Home');

  const renderContent = () => {
    switch (activeTab) {
      case 'Home':
        return <Home />;
      // Diğer bileşenleri burada ekleyin
      default:
        return <Home />;
    }
  };

  return (
    <div className="container">
      <div className="sidebar">
        <button className={`sidebar-item ${activeTab === 'Home' ? 'active' : ''}`} onClick={() => setActiveTab('Home')}>
          <FaHome className="icon" /> Home
        </button>
        <button className={`sidebar-item ${activeTab === 'Inbox' ? 'active' : ''}`} onClick={() => setActiveTab('Inbox')}>
          <FaInbox className="icon" /> Inbox
        </button>
      </div>
      <div className="main-content">
        {renderContent()}
      </div>
    </div>
  );
};

export default App;
