import React, { useState, useEffect, useRef } from 'react';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaPlus,
  FaLink,
  FaStar,
  FaEdit,
  FaLock,
  FaSave,
  FaTrashAlt,
  FaShareAlt,
} from 'react-icons/fa';
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CreateTaskModal from '../Modal/CreateTaskModal';
import './EverythingView.css'; // CSS dosyası

const EverythingView = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları (list, board, calendar)
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal açma/kapatma kontrolü
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Menü açma/kapatma durumu
  const dropdownRef = useRef(null); // Dropdown menüsü için referans oluşturma

  // Dropdown menüyü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation(); // Menü dışında başka yere tıklandığında menü kapanmasın
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    // Pencereye tıklama olayını ekle
    window.addEventListener('click', handleClickOutside);

    // Bileşen temizlendiğinde olay dinleyicisini kaldır
    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  // Yeni görev oluşturma işlemi
  const handleCreateTask = (newTask) => {
    setTasks([...tasks, newTask]); // Yeni görevi mevcut görev listesine ekle
  };

  // Filtreleme işlemi
  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  // Görünümler arasında geçiş
  const renderView = () => {
    if (filteredTasks.length === 0) {
      return <div>No tasks</div>;
    }

    switch (viewMode) {
      case 'list':
        return <TaskList tasks={filteredTasks} />;
      case 'board':
        return <BoardView tasks={filteredTasks} />;
      case 'calendar':
        return <CalendarView tasks={filteredTasks} />;
      default:
        return <TaskList tasks={filteredTasks} />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => setViewMode('list')}
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')}
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')}
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn">
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Dropdown menü */}
      {isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <button><FaEdit /> Rename</button>
          <button><FaLink /> Copy link to view</button>
          <button><FaStar /> Add to favorites</button>
          <button><FaEdit /> Customize view</button>
          <div className="toggle">
            <span><FaLock /> Pin view</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span><FaLock /> Private view</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span><FaSave /> Autosave for me</span>
            <input type="checkbox" />
          </div>
          <div className="submenu">
            <button><FaSave /> Export view</button>
            <button><FaEdit /> Duplicate view</button>
          </div>
          <button><FaTrashAlt /> Delete view</button>
          <button><FaShareAlt /> Sharing & Permissions</button>
        </div>
      )}

      {/* Arama ve Görev Ekleme */}
      <div className="task-controls">
        <input
          type="text"
          placeholder="Search tasks..."
          className="search-bar"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <button className="add-task-btn" onClick={() => setIsModalOpen(true)}>
          <FaPlus /> Add Task
        </button>
      </div>

      {/* Seçilen görünümde görevleri göster */}
      <div className="everything-content">{renderView()}</div>

      {/* Görev oluşturma modalı */}
      {isModalOpen && (
        <CreateTaskModal onClose={() => setIsModalOpen(false)} onCreate={handleCreateTask} />
      )}
    </div>
  );
};

export default EverythingView;
