import React, { useState } from "react";
import "./AssignedToMe.css"; // Eƒüer CSS d√ºzenlemelerin varsa

const AssignedToMe = () => {
  const [showModal, setShowModal] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);

  const handleAddTask = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleRemoveCard = () => {
    console.log("Card removed");
    setShowDropdown(false);
  };

  return (
    <div className="assigned-to-me card">
      <div className="header">
        <h2>Assigned to me</h2>
        <div className="header-icons">
          <button className="icon-button" title="Add task" onClick={handleAddTask}>
            <span role="img" aria-label="add-task">+</span>
          </button>
          <button className="icon-button" title="Expand" onClick={() => console.log("Expand")}>
            <span role="img" aria-label="expand">‚õ∂</span>
          </button>
          <div className="dropdown">
            <button className="icon-button" title="More" onClick={toggleDropdown}>
              <span role="img" aria-label="more">...</span>
            </button>
            {showDropdown && (
              <div className="dropdown-content">
                <button className="remove-card-button" onClick={handleRemoveCard}>
                  <span role="img" aria-label="trash">üóëÔ∏è</span>
                  Remove card
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="content">
        <div className="empty-state">
          <span role="img" aria-label="check" className="checkmark">‚úîÔ∏è</span>
          <p>Tasks assigned to you will appear here.</p>
          <button className="add-task-button" onClick={handleAddTask}>
            + Add task
          </button>
        </div>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Task Modal</h2>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssignedToMe;
