import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaList, FaTh, FaCalendarAlt, FaPlus, FaSearch } from 'react-icons/fa'; // İkonlar
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView'; // Yeni görünüm eklemek için
import CreateTaskModal from '../Modal/CreateTaskModal';
import './EverythingView.css'; // CSS dosyası

const EverythingView = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları (list, board, calendar, custom view)
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal açma/kapatma kontrolü

  const userInfo = JSON.parse(localStorage.getItem('userInfo'));

  // API'den görevleri çekme
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        if (!userInfo || !userInfo.token) {
          console.error('No valid user info or token found in localStorage');
          return;
        }

        const config = {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        };

        const { data } = await axios.get('/api/tasks', config);

        if (!data) {
          throw new Error('API returned undefined data');
        }

        setTasks(data);
      } catch (error) {
        console.error(
          'Error fetching tasks:',
          error.message || error.response?.data?.message || 'Unknown error'
        );
      }
    };

    fetchTasks();
  }, [userInfo]);

  // Yeni görev eklendiğinde state'i güncelleme
  const handleCreateTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  // Filtreleme işlemi
  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  // View moduna göre ilgili komponenti render etme
  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <TaskList tasks={filteredTasks} />;
      case 'board':
        return <BoardView tasks={filteredTasks} />;
      case 'calendar':
        return <CalendarView tasks={filteredTasks} />;
      case 'view':
        return <CustomView />; // Yeni görünüm ekleme sayfası
      default:
        return <TaskList tasks={filteredTasks} />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          <button className={`view-btn ${viewMode === 'list' ? 'active' : ''}`} onClick={() => setViewMode('list')}>
            <FaList /> List
          </button>
          <button className={`view-btn ${viewMode === 'board' ? 'active' : ''}`} onClick={() => setViewMode('board')}>
            <FaTh /> Board
          </button>
          <button className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`} onClick={() => setViewMode('calendar')}>
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => setViewMode('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Filtreleme */}
      <div className="filters">
        <button>Group: Status</button>
        <button>Subtasks: Collapse all</button>
        <button>Columns</button>
        <button>Filters</button>
        <button>Show Closed</button>
        <button>Hide</button>
      </div>

      {/* Arama ve Görev Ekleme */}
      <div className="task-controls">
        <input
          type="text"
          placeholder="Search tasks..."
          className="search-bar"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <button className="add-task-btn" onClick={() => setIsModalOpen(true)}>
          <FaPlus /> Add Task
        </button>
      </div>

      {/* Seçilen görünümde görevleri göster */}
      <div className="everything-content">{renderView()}</div>

      {/* Görev oluşturma modalı */}
      {isModalOpen && (
        <CreateTaskModal onClose={() => setIsModalOpen(false)} onCreate={handleCreateTask} />
      )}
    </div>
  );
};

export default EverythingView;
