import React, { useState, useEffect, useRef } from 'react';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaPlus,
  FaLink,
  FaStar,
  FaEdit,
  FaLock,
  FaSave,
  FaTrashAlt,
  FaShareAlt,
} from 'react-icons/fa'; // İkonlar
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView'; // Yeni görünüm eklemek için
import CreateTaskModal from '../Modal/CreateTaskModal';
import './EverythingView.css'; // CSS dosyası

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları (list, board, calendar, custom view)
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Menü açma/kapatma durumu
  const dropdownRef = useRef(null); // Dropdown menüsü için referans oluşturma

  // Dropdown menüyü açma/kapatma (sadece List görünümünde)
  const toggleDropdown = (e) => {
    e.stopPropagation();
    if (viewMode === 'list') {
      setIsDropdownOpen(!isDropdownOpen); // Sadece List görünümünde menüyü aç/kapa
    }
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    window.addEventListener('click', handleClickOutside);

    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  // View moduna göre ilgili komponenti render etme
  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <TaskList />;
      case 'board':
        return <BoardView />;
      case 'calendar':
        return <CalendarView />;
      case 'view':
        return <CustomView />;
      default:
        return <TaskList />;
    }
  };

  // List butonuna tıklandığında hem görünümü değiştir hem menüyü aç
  const handleListClick = (e) => {
    e.stopPropagation();
    setViewMode('list');
    setIsDropdownOpen(true); // List görünümüne geçtiğinde menüyü aç
  };

  // View değiştiğinde menüyü otomatik kapatma ve List'e dönüldüğünde Dropdown açılmasını sağlama
  const handleViewChange = (newView) => {
    setViewMode(newView);
    setIsDropdownOpen(false); // Diğer görünümlere geçerken menüyü kapat
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={handleListClick} // List butonuna tıklayınca menü açılacak ve List görünümüne geçilecek
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => handleViewChange('board')} // Board görünümüne geçiş
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => handleViewChange('calendar')} // Calendar görünümüne geçiş
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => handleViewChange('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Menü sadece List görünümünde açılacak */}
      {viewMode === 'list' && isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <button><FaEdit /> Rename</button>
          <button><FaLink /> Copy link to view</button>
          <button><FaStar /> Add to favorites</button>
          <button><FaEdit /> Customize view</button>
          <div className="toggle">
            <span><FaLock /> Pin view</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span><FaLock /> Private view</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span><FaSave /> Autosave for me</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span>Set as default view</span>
            <input type="checkbox" />
          </div>
          <div className="submenu">
            <button><FaSave /> Export view</button>
            <button><FaEdit /> Duplicate view</button>
          </div>
          <button><FaTrashAlt /> Delete view</button>
          <button><FaShareAlt /> Sharing & Permissions</button>
        </div>
      )}

      {/* Seçilen görünümde içerik render edilir */}
      <div className="everything-content">{renderView()}</div>
    </div>
  );
};

export default EverythingView;
