import React, { useState } from 'react';
import './Recents.css';

const Recents = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [projects, setProjects] = useState([]);
  const [projectName, setProjectName] = useState("");

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const addProject = () => {
    if (projectName.trim() === "") return;
    setProjects([...projects, { name: projectName }]);
    setProjectName("");
  };

  const removeProject = (index) => {
    const updatedProjects = projects.filter((_, i) => i !== index);
    setProjects(updatedProjects);
  };

  return (
    <div className="recents card">
      <div className="header">
        <h2>Recents</h2>
        <div className="actions">
          <button className="icon-button">+</button>
          <div className="settings">
            <button className="icon-button" onClick={toggleDropdown}>...</button>
            {showDropdown && (
              <div className="dropdown">
                <button className="remove-card-button">Remove card</button>
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="empty-state">
        {projects.length === 0 ? (
          <>
            <p>No recent projects available.</p>
            <input
              type="text"
              placeholder="Enter project name"
              value={projectName}
              onChange={(e) => setProjectName(e.target.value)}
            />
            <button className="create-project" onClick={addProject}>+ Create New Project</button>
          </>
        ) : (
          <>
            {projects.map((project, index) => (
              <div key={index} className="project-item">
                <span>{project.name} ‚Ä¢ in Project Notes</span>
                <div className="project-actions">
                  <button className="icon-button" title="Open in new tab">üîó</button>
                  <button className="icon-button" title="Copy link">üìã</button>
                  <button className="icon-button" title="Edit project">‚úèÔ∏è</button>
                  <button className="icon-button" onClick={() => removeProject(index)} title="Delete project">üóëÔ∏è</button>
                </div>
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};

export default Recents;
