import React, { useState } from 'react';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaPlus,
} from 'react-icons/fa';
import List from './List'; // List component
import Board from './Board'; // Board component
import Calendar from './Calendar'; // Calendar component
import CustomView from './CustomView'; // Custom view component
import './EverythingView.css'; // CSS dosyası

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list'); // Varsayılan olarak 'list' görünümü

  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <List />;
      case 'board':
        return <Board />;
      case 'calendar':
        return <Calendar />;
      case 'view':
        return <CustomView />;
      default:
        return <List />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => setViewMode('list')}
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')}
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')}
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => setViewMode('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Seçilen görünümün içerikleri */}
      <div className="everything-content">{renderView()}</div>
    </div>
  );
};

export default EverythingView;
