// src/components/TaskForm.js
import React, { useState, useRef, useEffect } from 'react';
import './TaskForm.css';

const TaskForm = () => {
  const [showTaskForm, setShowTaskForm] = useState(false);
  const taskFormRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (taskFormRef.current && !taskFormRef.current.contains(event.target)) {
        setShowTaskForm(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleTaskForm = () => {
    setShowTaskForm(!showTaskForm);
  };

  return (
    <div className="task-form-container">
      <button onClick={toggleTaskForm} className="add-task-button">
        + Add Task
      </button>

      {showTaskForm && (
        <div className="task-form" ref={taskFormRef}>
          <h3>Add Task</h3>
          <label>
            Task Name:
            <input type="text" placeholder="Enter task name" />
          </label>
          <label>
            Description:
            <textarea placeholder="Add description"></textarea>
          </label>
          <label>
            Due Date:
            <input type="date" />
          </label>
          <label>
            Priority:
            <select>
              <option>Low</option>
              <option>Medium</option>
              <option>High</option>
            </select>
          </label>
          <div className="task-form-actions">
            <button className="close-task-button" onClick={() => setShowTaskForm(false)}>
              Close
            </button>
            <button className="create-task-button">Create Task</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TaskForm;
