import React, { useState } from 'react';
import './Chat.css';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, input]);
      setInput('');
    }
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <div className="sidebar-item active">Chat</div>
        <div className="sidebar-item">Activity</div>
        <div className="sidebar-item">Follow-Up</div>
        <div className="sidebar-item">To-Do List</div>
        <div className="sidebar-item">Chats</div>
      </div>
      <div className="chat-main">
        <div className="chat-header">
          <h2>Kaan AKSU's Workspace</h2>
          <div className="chat-header-icons">
            <span title="Settings">⚙️</span>
            <span title="Info">ℹ️</span>
          </div>
        </div>
        <div className="chat-body">
          {messages.length === 0 ? (
            <div className="empty-chat">
              <p>Inbox Zero</p>
              <p>Congratulations! You've read all your important messages.</p>
              <button className="btn">Got it</button>
            </div>
          ) : (
            messages.map((msg, index) => <div key={index} className="chat-message">{msg}</div>)
          )}
        </div>
        <div className="chat-footer">
          <input
            type="text"
            placeholder="Write here..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={handleSend}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
