import React, { useState } from "react";
import "./AssignedToMe.css";

const AssignedToMe = () => {
  const [showModal, setShowModal] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showTooltipAdd, setShowTooltipAdd] = useState(false);
  const [showTooltipSettings, setShowTooltipSettings] = useState(false);

  const handleAddTask = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleRemoveCard = () => {
    console.log("Card removed");
    setShowDropdown(false);
  };

  return (
    <div className="assigned-to-me card">
      <div className="header">
        <h2>Assigned to me</h2>
        <div className="header-icons">
          <div className="icon-wrapper">
            <button
              className="icon-button"
              onMouseEnter={() => setShowTooltipAdd(true)}
              onMouseLeave={() => setShowTooltipAdd(false)}
              onClick={handleAddTask}
            >
              <span role="img" aria-label="add-task">+</span>
            </button>
            {showTooltipAdd && <div className="tooltip">Add task</div>}
          </div>

          <div className="icon-wrapper">
            <button
              className="icon-button"
              onMouseEnter={() => setShowTooltipSettings(true)}
              onMouseLeave={() => setShowTooltipSettings(false)}
              onClick={toggleDropdown}
            >
              <span role="img" aria-label="more-settings">...</span>
            </button>
            {showTooltipSettings && <div className="tooltip">More settings</div>}
          </div>
          
          {showDropdown && (
            <div className="dropdown">
              <button className="remove-card-button" onClick={handleRemoveCard}>
                <span role="img" aria-label="trash">üóëÔ∏è</span> Remove card
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="content">
        <div className="empty-state">
          <span role="img" aria-label="check" className="checkmark">‚úîÔ∏è</span>
          <p>Tasks assigned to you will appear here.</p>
          <button className="add-task-button" onClick={handleAddTask}>
            + Add task
          </button>
        </div>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Task Modal</h2>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssignedToMe;
