import React, { useState } from 'react';
import './Inbox.css'; // CSS dosyasƒ±nƒ± d√ºzenle

const Inbox = () => {
  const [activeTab, setActiveTab] = useState('Inbox');
  const [showFilterMenu, setShowFilterMenu] = useState(false);
  const [showCustomizeMenu, setShowCustomizeMenu] = useState(false);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="inbox-container">
      {/* √úst Men√º */}
      <div className="tabs">
        {['Inbox', 'Important', 'Other', 'Snoozed', 'Cleared'].map((tab) => (
          <button
            key={tab}
            className={`tab-button ${activeTab === tab ? 'active' : ''}`}
            onClick={() => handleTabClick(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Filtre ve Customize Men√º */}
      <div className="toolbar">
        <button className="filter-button" onClick={() => setShowFilterMenu(!showFilterMenu)}>
          Filter
        </button>
        <button className="clear-all">Clear all</button>
        <button className="customize-button" onClick={() => setShowCustomizeMenu(!showCustomizeMenu)}>
          Customize
        </button>
      </div>

      {/* Filtre men√ºs√º */}
      {showFilterMenu && (
        <div className="filter-menu">
          <p>Assigned to me</p>
          <p>Mentioning me</p>
          <p>Unread only</p>
        </div>
      )}

      {/* Customize men√ºs√º */}
      {showCustomizeMenu && (
        <div className="customize-menu">
          <h4>Customize Inbox</h4>
          <p>Group by date</p>
          <div className="layout-options">
            <p>Display mode</p>
            <button className="layout-option">Fullscreen</button>
            <button className="layout-option">Inline</button>
          </div>
        </div>
      )}

      {/* ƒ∞√ßerik */}
      <div className="inbox-content">
        {activeTab === 'Cleared' ? (
          <div className="inbox-zero">
            <div className="envelope-icon">üì≠</div>
            <h2>You don‚Äôt have any cleared notifications</h2>
            <p>Learn how to clear in Inbox.</p>
          </div>
        ) : (
          <p>No content in this section.</p>
        )}
      </div>
    </div>
  );
};

export default Inbox;
