import React from 'react';
import { FaPlus, FaUserCircle } from 'react-icons/fa';
import './Sidebar.css';

const Sidebar = ({ activeTab, setActiveTab }) => {
  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2>Chat</h2>
        <button className="new-chat-btn">
          <FaPlus /> New
        </button>
      </div>
      <div className="sidebar-items">
        <h4>Chats</h4>
        <ul>
          <li
            className={activeTab === 'Workspace' ? 'active' : ''}
            onClick={() => setActiveTab('Workspace')}
          >
            Kaan AKSÜT's Workspace
          </li>
          <li onClick={() => setActiveTab('Add Chat')}>Add Chat</li>
        </ul>
        <h4>Direct messages</h4>
        <ul>
          <li
            className={activeTab === 'Direct' ? 'active' : ''}
            onClick={() => setActiveTab('Direct')}
          >
            <FaUserCircle /> Kaan AKSÜT — You
          </li>
          <li onClick={() => setActiveTab('Invite')}>Invite members</li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
