import React, { useState } from 'react';
import './Inbox.css'; // CSS dosyasƒ±nƒ± unutmayƒ±n

const Inbox = () => {
  const [activeTab, setActiveTab] = useState('Inbox');
  const [showFilter, setShowFilter] = useState(false);

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const toggleFilter = () => {
    setShowFilter(!showFilter);
  };

  return (
    <div className="inbox-container">
      <div className="tabs">
        {['Inbox', 'Important', 'Other', 'Snoozed', 'Cleared'].map((tab) => (
          <button
            key={tab}
            className={`tab-button ${activeTab === tab ? 'active' : ''}`}
            onClick={() => handleTabClick(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="search-bar">
        <input type="text" placeholder="Search..." />
        <div className="search-icons">
          <button className="filter-button" onClick={toggleFilter}>Filter</button>
          <button className="clear-button">Clear all</button>
          <button className="customize-button">Customize</button>
        </div>
      </div>

      {showFilter && (
        <div className="filter-dropdown">
          <div className="filter-item">
            üë§ Assigned to me
            <span className="shortcut">Shift + 1</span>
          </div>
          <div className="filter-item">
            @ Mentioning me
            <span className="shortcut">Shift + 2</span>
          </div>
          <div className="filter-item">
            üì® Unread only
            <span className="shortcut">Shift + 3</span>
          </div>
        </div>
      )}

      <div className="inbox-content">
        {activeTab === 'Inbox' && (
          <div className="inbox-zero">
            <div className="envelope-icon">‚úâÔ∏è</div>
            <h2>Inbox Zero</h2>
            <p>Congratulations! You've read all your important messages.</p>
            <button className="btn">Got it</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Inbox;
