import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // React Router importları
import Sidebar from './components/Sidebar'; // Sidebar bileşeni
import ChatContent from './components/ChatContent'; // ChatContent bileşeni
import Home from './components/Home';
import Inbox from './components/Inbox';
import Chat from './components/Chat';
import Docs from './components/Docs';
import Dashboards from './components/Dashboards';
import Timesheets from './components/Timesheets';
import More from './components/More';
import EverythingPage from './pages/EverythingPage'; // EverythingPage bileşeni
import ListView from './components/ListView'; // Yeni eklenen ListView
import BoardView from './components/BoardView'; // Yeni eklenen BoardView
import GanttView from './components/GanttView'; // Yeni eklenen GanttView
import CalendarView from './components/CalendarView'; // Yeni eklenen CalendarView
import TableView from './components/TableView'; // Yeni eklenen TableView

const App = () => {
  const [activeTab, setActiveTab] = useState('Everything'); // Başlangıçta Everything sekmesi aktif
  const [isMenuOpen, setIsMenuOpen] = useState(false); // Menü durumu
  const menuRef = useRef(null);

  // Sayfanın herhangi bir yerine tıklanınca menüyü kapat
  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleOutsideClick);
    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, []);

  // İçeriği seçilen aktif tab'a göre render et
  const renderContent = () => {
    switch (activeTab) {
      case 'Home':
        return <Home />;
      case 'Inbox':
        return <Inbox />;
      case 'Chat':
        return <Chat />;
      case 'Docs':
        return <Docs />;
      case 'Dashboards':
        return <Dashboards />;
      case 'Timesheets':
        return <Timesheets />;
      case 'More':
        return <More />;
      case 'Everything':
        return <EverythingPage />; // EverythingPage'i burada render ediyoruz
      default:
        return <Home />;
    }
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <Router>
      <div className="app-container">
        <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />
        <div className="main-content">
          <div className="top-bar">
            <input type="text" placeholder="Search..." className="search-bar" />
            <div className="top-bar-icons">
              <span title="Notifications">🔔</span>
              <span title="Profile">👤</span>
            </div>
          </div>
          <div className="content">
            <Routes>
              <Route path="/list" element={<ListView />} />
              <Route path="/board" element={<BoardView />} />
              <Route path="/gantt" element={<GanttView />} />
              <Route path="/calendar" element={<CalendarView />} />
              <Route path="/table" element={<TableView />} />
              <Route path="/" element={renderContent()} />
            </Routes>
            <ChatContent activeTab={activeTab} /> {/* ChatContent bileşeni */}
          </div>
        </div>
      </div>
    </Router>
  );
};

export default App;
