import React, { useState, useEffect, useRef } from 'react';
import './AssignedToMe.css';
import { FaPlus, FaTrash } from 'react-icons/fa';
import TaskModal from './TaskModal';

const AssignedToMe = () => {
  const [showSettings, setShowSettings] = useState(false);
  const [showTaskModal, setShowTaskModal] = useState(false);
  const settingsRef = useRef(null);

  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  const removeCard = () => {
    alert("Card removed!");
  };

  const toggleTaskModal = () => {
    setShowTaskModal(!showTaskModal);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (settingsRef.current && !settingsRef.current.contains(event.target)) {
        setShowSettings(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [settingsRef]);

  return (
    <div className="assigned-to-me-container">
      <div className="assigned-to-me-header">
        <h2>Assigned to me</h2>
        <button className="icon-btn" onClick={toggleTaskModal} title="Add task">
          <FaPlus />
        </button>
      </div>

      {showTaskModal && <TaskModal closeModal={toggleTaskModal} />}

      {showSettings && (
        <div className="more-settings-menu" ref={settingsRef}>
          <button className="settings-item" onClick={removeCard}>
            <FaTrash className="trash-icon" /> Remove card
          </button>
        </div>
      )}

      <div className="assigned-to-me-content">
        <div className="icon-placeholder">
          <span>✔️</span>
        </div>
        <p>Tasks assigned to you will appear here. <a href="#">Learn more</a></p>
        <button className="add-task-btn" onClick={toggleTaskModal}>+ Add task</button>
      </div>
    </div>
  );
};

export default AssignedToMe;
