import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Home from './components/Home'; // Home bileşenini ekliyoruz
import { FaHome, FaInbox, FaComments, FaFileAlt, FaChartBar, FaClock, FaEllipsisH, FaGlobe, FaEye, FaPlus } from 'react-icons/fa';

const App = () => {
  const [activeTab, setActiveTab] = useState('Home');

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <Router>
      <div className="container">
        <div className="sidebar">
          <button className={`sidebar-item ${activeTab === 'Home' ? 'active' : ''}`} onClick={() => handleTabClick('Home')}>
            <FaHome className="icon" /> Home
          </button>
          <button className={`sidebar-item ${activeTab === 'Inbox' ? 'active' : ''}`} onClick={() => handleTabClick('Inbox')}>
            <FaInbox className="icon" /> Inbox
          </button>
          <button className={`sidebar-item ${activeTab === 'Chat' ? 'active' : ''}`} onClick={() => handleTabClick('Chat')}>
            <FaComments className="icon" /> Chat
          </button>
          <button className={`sidebar-item ${activeTab === 'Docs' ? 'active' : ''}`} onClick={() => handleTabClick('Docs')}>
            <FaFileAlt className="icon" /> Docs
          </button>
          <button className={`sidebar-item ${activeTab === 'Dashboards' ? 'active' : ''}`} onClick={() => handleTabClick('Dashboards')}>
            <FaChartBar className="icon" /> Dashboards
          </button>
          <button className={`sidebar-item ${activeTab === 'Timesheets' ? 'active' : ''}`} onClick={() => handleTabClick('Timesheets')}>
            <FaClock className="icon" /> Timesheets
          </button>
          <button className={`sidebar-item ${activeTab === 'More' ? 'active' : ''}`} onClick={() => handleTabClick('More')}>
            <FaEllipsisH className="icon" /> More
          </button>
        </div>

        <div className="main-content">
          <Switch>
            <Route path="/" exact component={Home} />
            {/* Diğer sekmelere yönlendirme ekleyebilirsin */}
          </Switch>
        </div>
      </div>
    </Router>
  );
};

export default App;
