        return <Chat />;
      case 'Docs':
        return <Docs />;
      case 'Dashboards':
        return <Dashboards />;
      case 'Timesheets':
        return <Timesheets />;
      case 'More':
        return <More />;
      case 'Everything':
        return <EverythingPage />;
