          Dashboards
        </button>
        <button className={`sidebar-item ${activeTab === 'Timesheets' ? 'active' : ''}`} onClick={() => setActiveTab('Timesheets')}>
          Timesheets
        </button>
        <button className={`sidebar-item ${activeTab === 'More' ? 'active' : ''}`} onClick={() => setActiveTab('More')}>
          More
        </button>
        <button className={`sidebar-item ${activeTab === 'Everything' ? 'active' : ''}`} onClick={() => setActiveTab('Everything')}>
          Everything
        </button>
      </div>
      <div className="main-content">
        <div className="top-bar">
