import React, { useState, useRef, useEffect } from 'react';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaLink,
  FaStar,
  FaEdit,
  FaLock,
  FaSave,
  FaTrashAlt,
  FaShareAlt,
  FaLayerGroup,
  FaFilter,
  FaSort,
  FaEye,
  FaPlus,
} from 'react-icons/fa';
import CreateTaskModal from '../Modal/CreateTaskModal'; // CreateTaskModal bileşenini içe aktarıyoruz
import './ListView.css'; // CSS dosyası

const ListView = ({ tasks, handleCreateTask, filter, setFilter }) => {
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal açma/kapatma kontrolü
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Menü açma/kapatma durumu
  const [selectedTask, setSelectedTask] = useState(null); // Hangi görev için menü açıldığını takip etme
  const dropdownRef = useRef(null); // Dropdown menüsü için referans oluşturma

  // Dropdown menüyü açma/kapatma
  const toggleDropdown = (task, e) => {
    e.stopPropagation();
    setSelectedTask(task);
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    // Pencereye tıklama olayını ekle
    window.addEventListener('click', handleClickOutside);
    
    // Bileşen temizlendiğinde olay dinleyicisini kaldır
    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className="list-view-container">
      {/* Üst Menü Butonları */}
      <div className="view-options">
        <button className="view-btn" onClick={toggleDropdown}>
          <FaList /> List
        </button>
        <button className="view-btn">
          <FaTh /> Board
        </button>
        <button className="view-btn">
          <FaCalendarAlt /> Calendar
        </button>
      </div>

      {/* Filtre Butonları */}
      <div className="filter-buttons">
        <button className="filter-btn">
          <FaLayerGroup /> Group: Status
        </button>
        <button className="filter-btn">Subtasks: Collapse all</button>
        <button className="filter-btn">
          <FaFilter /> Filters
        </button>
        <button className="filter-btn">
          <FaSort /> Sort
        </button>
        <button className="filter-btn">
          <FaEye /> Show closed
        </button>
        <button className="filter-btn">Hide</button>
      </div>

      {/* Arama ve Görev Ekleme */}
      <div className="task-controls-right">
        <input
          type="text"
          placeholder="Search tasks..."
          className="search-bar"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <button className="add-task-btn" onClick={() => setIsModalOpen(true)}>
          <FaPlus /> Add Task
        </button>
      </div>

      {/* Görevlerin Listesi */}
      <div className="task-list">
        {tasks.map((task) => (
          <div key={task.id} className="task-item" onClick={(e) => toggleDropdown(task, e)}>
            {task.title}
          </div>
        ))}
      </div>

      {/* Açılır Menü */}
      {isDropdownOpen && selectedTask && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <ul>
            <li><FaEdit /> Edit Task</li>
            <li><FaTrashAlt /> Delete Task</li>
            <li><FaSave /> Complete Task</li>
            <li><FaLink /> Copy link to task</li>
            <li><FaStar /> Add to favorites</li>
          </ul>
        </div>
      )}

      {/* Görev oluşturma modalı */}
      {isModalOpen && (
        <CreateTaskModal onClose={() => setIsModalOpen(false)} onCreate={handleCreateTask} />
      )}
    </div>
  );
};

export default ListView;
