import React, { useState } from 'react';
import EverythingToolbar from './EverythingToolbar';
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView'; // Yeni görünüm eklemek için

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list'); // Varsayılan görünüm

  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <TaskList />;
      case 'board':
        return <BoardView />;
      case 'calendar':
        return <CalendarView />;
      case 'view':
        return <CustomView />; // Yeni görünüm ekleme sayfası
      default:
        return <TaskList />;
    }
  };

  return (
    <div className="everything-container">
      <EverythingToolbar viewMode={viewMode} setViewMode={setViewMode} />
      <div className="view-content">{renderView()}</div>
    </div>
  );
};

export default EverythingView;
