import React, { useState, useRef, useEffect } from 'react';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaLink,
  FaStar,
  FaEdit,
  FaLock,
  FaSave,
  FaTrashAlt,
  FaShareAlt,
} from 'react-icons/fa';
import './ListView.css'; // CSS dosyası

const ListView = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Dropdown menü kontrolü
  const dropdownRef = useRef(null); // Dropdown menüsü için referans oluşturma

  // Dropdown menüyü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation();
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    // Tıklama olayını dinleme
    window.addEventListener('click', handleClickOutside);

    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className="list-view-container">
      {/* Üst menü (List, Board, Calendar vs.) */}
      <div className="view-options">
        <button className="view-btn" onClick={toggleDropdown}>
          <FaList /> List
        </button>
        <button className="view-btn">
          <FaTh /> Board
        </button>
        <button className="view-btn">
          <FaCalendarAlt /> Calendar
        </button>
      </div>

      {/* Dropdown Menü (List butonuna tıklanınca açılacak) */}
      {isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <ul>
            <li>
              <FaEdit /> Rename
            </li>
            <li>
              <FaLink /> Copy link to view
            </li>
            <li>
              <FaStar /> Add to favorites
            </li>
            <li>
              <FaEdit /> Customize view
            </li>
            <li>
              <FaLock /> Pin view
            </li>
            <li>
              <FaSave /> Autosave for me
            </li>
            <li>
              <FaTrashAlt /> Delete view
            </li>
            <li>
              <FaShareAlt /> Sharing & Permissions
            </li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default ListView;
