import React, { useState } from 'react';
import {
  FaPlus,
} from 'react-icons/fa'; // İkonlar
import './BoardView.css'; // CSS dosyası

const BoardView = () => {
  const [filter, setFilter] = useState(''); // Arama filtresi
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal açma/kapatma kontrolü

  return (
    <div className="board-view-container">
      {/* Arama ve Görev Ekleme Alanı */}
      <div className="task-controls">
        <input
          type="text"
          placeholder="Search tasks..."
          className="search-bar"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <button className="add-task-btn" onClick={() => setIsModalOpen(true)}>
          <FaPlus /> Add Task
        </button>
      </div>

      {/* Boş Görev Listesi Gösterimi */}
      <div className="no-tasks-container">
        <div className="no-tasks-content">
          <img
            src="empty-tasks.png"
            alt="No tasks"
            className="no-tasks-image"
          />
          <p className="no-tasks-text">You don't have any tasks assigned to you.</p>
          <button className="create-task-btn">+ Create New Task</button>
          <a href="#" className="turn-off-me-mode">Turn off Me Mode</a>
        </div>
      </div>
    </div>
  );
};

export default BoardView;
