// src/components/EverythingView/EverythingView.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EverythingView.css';
import CreateTask from '../CreateTask';

const EverythingView = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');

  const userInfo = JSON.parse(localStorage.getItem('userInfo'));

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        if (!userInfo || !userInfo.token) {
          console.error('No valid user info or token found in localStorage');
          return; // Eğer token yoksa, istek yapılmaz.
        }

        const config = {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        };

        const { data } = await axios.get('/api/tasks', config);

        if (!data) {
          throw new Error('API returned undefined data');
        }

        setTasks(data); // Veriler başarılı bir şekilde alınırsa state güncellenir.
      } catch (error) {
        // Eğer hata alırsak, detayları console'a yazdır.
        console.error('Error fetching tasks:', error.message || error.response?.data?.message || 'Unknown error');
      }
    };

    fetchTasks();
  }, [userInfo]);

  // Yeni görev eklendiğinde state'i güncelleyelim
  const handleTaskCreated = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  // Filtrelenmiş görevleri alma
  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="everything-view">
      <div className="everything-header">
        <h2>Everything</h2>
        <input
          type="text"
          placeholder="Search tasks..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="search-bar"
        />
      </div>

      {/* Görev oluşturma formunu ekleyelim */}
      <CreateTask onTaskCreated={handleTaskCreated} />

      <div className="everything-content">
        <div className="task-list">
          {filteredTasks.length > 0 ? (
            filteredTasks.map((task) => (
              <div key={task._id} className="task-item">
                <h3>{task.title}</h3>
                <p>Status: {task.status}</p>
                <p>Due Date: {task.dueDate}</p>
              </div>
            ))
          ) : (
            <p>No tasks found.</p> // Görev yoksa mesaj göster.
          )}
        </div>
      </div>
    </div>
  );
};

export default EverythingView;
