import React, { useState } from "react";
import "./AssignedToMe.css";

const AssignedToMe = () => {
  const [showModal, setShowModal] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showAddTaskTooltip, setShowAddTaskTooltip] = useState(false);
  const [showMoreSettingsTooltip, setShowMoreSettingsTooltip] = useState(false);

  const handleAddTask = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleRemoveCard = () => {
    console.log("Card removed");
    setShowDropdown(false);
  };

  return (
    <div className="assigned-to-me card">
      <div className="header">
        <h2>Assigned to me</h2>
        <div className="header-icons">
          {/* Add Task Icon */}
          <button
            className="icon-button"
            title="Add task"
            onMouseEnter={() => setShowAddTaskTooltip(true)}
            onMouseLeave={() => setShowAddTaskTooltip(false)}
            onClick={handleAddTask}
          >
            <span role="img" aria-label="add-task">+</span>
            {showAddTaskTooltip && <span className="tooltip">Add task</span>}
          </button>

          {/* More Settings Icon */}
          <div className="settings">
            <button
              className="icon-button"
              title="More settings"
              onMouseEnter={() => setShowMoreSettingsTooltip(true)}
              onMouseLeave={() => setShowMoreSettingsTooltip(false)}
              onClick={toggleDropdown}
            >
              <span role="img" aria-label="more-settings">...</span>
              {showMoreSettingsTooltip && <span className="tooltip">More settings</span>}
            </button>

            {showDropdown && (
              <div className="dropdown-content">
                <button className="remove-card" onClick={handleRemoveCard}>
                  üóëÔ∏è Remove card
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="content">
        <div className="empty-state">
          <span role="img" aria-label="check" className="checkmark">‚úîÔ∏è</span>
          <p>Tasks assigned to you will appear here.</p>
          <button className="add-task-button" onClick={handleAddTask}>
            + Add task
          </button>
        </div>
      </div>

      {/* Modal for Task Creation */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Task Modal</h2>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssignedToMe;
