// src/components/Everything.js
import React, { useState } from 'react';
import { EverythingViewButtons } from './EverythingViewButtons';
import { EverythingViewIcons } from './EverythingViewIcons';
import './everything.css';

// Örnek görev verisi
const initialTasks = [
  { id: 1, title: 'Task 1', status: 'In Progress', dueDate: '2024-10-05' },
  { id: 2, title: 'Task 2', status: 'Completed', dueDate: '2024-09-30' },
  { id: 3, title: 'Task 3', status: 'Pending', dueDate: '2024-10-10' },
];

const Everything = () => {
  const [tasks, setTasks] = useState(initialTasks);
  const [filter, setFilter] = useState('');

  const filteredTasks = tasks.filter(task => 
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="everything-view">
      <div className="everything-header">
        <h2>Everything</h2>
        <input 
          type="text" 
          className="search-bar" 
          placeholder="Search tasks..." 
          value={filter} 
          onChange={(e) => setFilter(e.target.value)} 
        />
      </div>
      <EverythingViewButtons />
      <EverythingViewIcons />
      <div className="everything-content">
        <div className="task-list">
          {filteredTasks.map(task => (
            <div key={task.id} className="task-item">
              <h3>{task.title}</h3>
              <p>Status: {task.status}</p>
              <p>Due Date: {task.dueDate}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Everything;
