import React, { useState, useRef, useEffect } from 'react';
import { FaLayerGroup, FaFilter, FaSort, FaEye, FaPlus } from 'react-icons/fa';
import './ListView.css'; 

const ListView = ({ tasks = [] }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); 
  const dropdownRef = useRef(null);

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setIsDropdownOpen(!isDropdownOpen);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    window.addEventListener('click', handleClickOutside);
    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className="list-view-container">
      <div className="filter-buttons">
        <button className="filter-btn"><FaLayerGroup /> Group: Status</button>
        <button className="filter-btn">Subtasks: Collapse all</button>
        <button className="filter-btn"><FaFilter /> Filters</button>
        <button className="filter-btn"><FaSort /> Sort</button>
        <button className="filter-btn"><FaEye /> Show closed</button>
        <button className="filter-btn">Hide</button>
      </div>

      <div className="task-list">
        {tasks.length > 0 ? (
          tasks.map((task) => (
            <div key={task.id} className="task-item" onClick={toggleDropdown}>
              {task.title}
            </div>
          ))
        ) : (
          <p>No tasks available.</p>
        )}
      </div>

      {/* Açılır Menü */}
      {isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <ul>
            <li>Edit Task</li>
            <li>Delete Task</li>
            <li>Complete Task</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default ListView;
