import React, { useState, useRef, useEffect } from 'react';
import './TaskList.css'; // TaskList için CSS'i buraya ekleyelim

const TaskList = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Menü açma/kapatma durumu
  const dropdownRef = useRef(null); // Açılır menü referansı

  // Dropdown menüyü açma/kapatma fonksiyonu
  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Sayfanın herhangi bir yerine tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="task-list-container">
      <h2>Task List</h2>
      {/* Task Options butonu */}
      <button className="list-options-btn" onClick={toggleDropdown}>
        List Options
      </button>
      {/* Açılır menü */}
      {isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <button>Rename</button>
          <button>Copy link to view</button>
          <button>Add to favorites</button>
          <button>Customize view</button>
          <button>Pin view</button>
          <button>Private view</button>
        </div>
      )}
      <p>No tasks available.</p>
    </div>
  );
};

export default TaskList;
