import React, { useState, useRef, useEffect } from 'react';
import './Home.css';
import Recents from './Recents';
import Agenda from './Agenda';
import MyWork from './MyWork';
import AssignedToMe from './AssignedToMe';

const Home = () => {
  const [projects, setProjects] = useState([]);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  // Boşluğa tıklandığında menülerin kapanmasını sağlamak için
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="home-page">
      <h1>Good evening, Kaan</h1>
      <div className="grid-container">
        {/* Recents bileşenini burada kullan */}
        <Recents projects={projects} setProjects={setProjects} />
        
        {/* Agenda bileşenini burada kullan */}
        <Agenda />
        
        {/* My Work bileşenini burada kullan */}
        <MyWork />
        
        {/* Assigned to Me bileşenini burada kullan */}
        <AssignedToMe />

        {/* Menünün açılma durumu */}
        {isMenuOpen && (
          <div className="task-menu" ref={menuRef}>
            <p>Task Menu Content</p>
          </div>
        )}
        
        {/* Butona basıldığında menüyü açma */}
        <button onClick={toggleMenu}>Open Menu</button>
      </div>
    </div>
  );
};

export default Home;
