import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import {
  FaList, FaTh, FaCalendarAlt, FaPlus, FaLink, FaStar, FaEdit, FaLock, FaSave, FaTrashAlt, FaShareAlt
} from 'react-icons/fa';
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView';
import ListView from './ListView'; // ListView bileşeni
import './EverythingView.css';

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Dropdown menü kontrolü
  const [tasks, setTasks] = useState([]); // Görevler için state
  const [filter, setFilter] = useState(''); // Filtreleme için state
  const dropdownRef = useRef(null); // Dropdown menüsü için referans
  const userInfo = JSON.parse(localStorage.getItem('userInfo')); // Kullanıcı bilgilerini alıyoruz

  // API'den görevleri çekme
  const fetchTasks = async () => {
    try {
      if (!userInfo || !userInfo.token) {
        console.error('No valid user info or token found in localStorage');
        return;
      }

      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`,
        },
      };

      const { data } = await axios.get('/api/tasks', config);

      if (!data || data.length === 0) {
        console.warn('No tasks found.');
        setTasks([]); // Eğer görev yoksa boş bir dizi ata
        return;
      }

      setTasks(data);
    } catch (error) {
      console.error(
        'Error fetching tasks:',
        error.message || error.response?.data?.message || 'Unknown error'
      );
      setTasks([]); // Hata durumunda boş bir dizi ata
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Dropdown menüsünü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation();
    if (viewMode === 'list') {
      setIsDropdownOpen(!isDropdownOpen); // Sadece list modunda açılacak
    }
  };

  // Boşluğa tıklayınca menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    window.addEventListener('click', handleClickOutside);

    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  // Filtreleme işlemi
  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  // Görünüm geçişlerini render etme
  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <ListView tasks={filteredTasks} filter={filter} setFilter={setFilter} />;
      case 'board':
        return <BoardView tasks={filteredTasks} />;
      case 'calendar':
        return <CalendarView tasks={filteredTasks} />;
      case 'view':
        return <CustomView />;
      default:
        return <TaskList tasks={filteredTasks} />;
    }
  };

  return (
    <div className="everything-container">
      <div className="everything-header">
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={toggleDropdown} // List butonuna tıklayınca menü açılacak
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')} // Board görünümüne geçiş
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')} // Calendar görünümüne geçiş
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => setViewMode('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Menü sadece List görünümünde açılacak */}
      {viewMode === 'list' && isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <ul>
            <li><FaEdit /> Rename</li>
            <li><FaLink /> Copy link to view</li>
            <li><FaStar /> Add to favorites</li>
            <li><FaEdit /> Customize view</li>
            <li><FaLock /> Pin view</li>
            <li><FaSave /> Autosave for me</li>
            <li><FaTrashAlt /> Delete view</li>
            <li><FaShareAlt /> Sharing & Permissions</li>
          </ul>
        </div>
      )}

      {/* Seçilen görünüm render edilecek */}
      <div className="everything-content">
        {renderView()}
      </div>
    </div>
  );
};

export default EverythingView;
