import React, { useState } from "react";
import "./AssignedToMe.css"; 

const AssignedToMe = () => {
  const [showModal, setShowModal] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showTooltip, setShowTooltip] = useState(false); // Tooltip için useState ekledik

  const handleAddTask = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleRemoveCard = () => {
    console.log("Card removed");
    setShowDropdown(false);
  };

  return (
    <div className="assigned-to-me card">
      <div className="header">
        <h2>Assigned to me</h2>
        <div className="header-icons">
          <button
            className="icon-button"
            title="Add task"
            onMouseEnter={() => setShowTooltip(true)}
            onMouseLeave={() => setShowTooltip(false)}
            onClick={handleAddTask}
          >
            <span role="img" aria-label="add-task">
              +
            </span>
            {showTooltip && <span className="tooltip">Add task</span>} {/* Tooltip ekliyoruz */}
          </button>
          <button
            className="icon-button"
            title="Expand"
            onClick={() => console.log("Expand")}
          >
            <span role="img" aria-label="expand">
              ⛶
            </span>
          </button>
          <div className="dropdown">
            <button
              className="icon-button"
              title="More"
              onMouseEnter={() => setShowTooltip(true)}
              onMouseLeave={() => setShowTooltip(false)}
              onClick={toggleDropdown}
            >
              <span role="img" aria-label="more">
                ...
              </span>
              {showTooltip && <span className="tooltip">More settings</span>}
            </button>
            {showDropdown && (
              <div className="dropdown-content">
                <button className="remove-card" onClick={handleRemoveCard}>
                  <span role="img" aria-label="trash">
                    🗑️
                  </span>
                  Remove card
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="content">
        <div className="empty-state">
          <span role="img" aria-label="check" className="checkmark">
            ✔️
          </span>
          <p>Tasks assigned to you will appear here.</p>
          <button className="add-task-button" onClick={handleAddTask}>
            + Add task
          </button>
        </div>
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Task Modal</h2>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssignedToMe;
