import React, { useState } from 'react';
import TaskModal from './TaskModal';
import './AssignedToMe.css'; // CSS dosyası gerektiğinde

const AssignedToMe = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [showSettings, setShowSettings] = useState(false);

  // Task ekleme modalını açma ve kapama
  const handleModalOpen = () => {
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  // ... düğmesine tıklanınca ayarları açma/kapama
  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  return (
    <div className="assigned-to-me">
      <div className="header">
        <h2>Assigned to me</h2>
        <div className="actions">
          <button className="add-task-button" onClick={handleModalOpen}>+</button>
          <button className="more-options-button" onClick={toggleSettings}>...</button>
          {showSettings && (
            <div className="dropdown">
              <button className="remove-button">Remove card</button>
            </div>
          )}
        </div>
      </div>

      {/* Eğer modal açıksa task ekleme modali görünür */}
      <TaskModal isOpen={isModalOpen} onClose={handleModalClose} />

      <div className="empty-state">
        <div className="icon">✔️</div>
        <p>Tasks assigned to you will appear here. <a href="#">Learn more</a></p>
        <button className="add-task" onClick={handleModalOpen}>+ Add task</button>
      </div>
    </div>
  );
};

export default AssignedToMe;
