import React, { useState, useRef, useEffect } from 'react';
import { FaList, FaTh, FaCalendarAlt, FaPlus } from 'react-icons/fa';
import TaskList from './TaskList'; // TaskList ile açılır menüyü burada göstereceğiz
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView';
import './EverythingView.css'; // CSS dosyası

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Menü açma/kapatma durumu
  const dropdownRef = useRef(null); // Dropdown menüsü için referans oluşturma

  // Dropdown menüyü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation();
    setIsDropdownOpen(!isDropdownOpen); // Menü durumunu değiştir
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false); // Dışarıya tıklanınca menüyü kapat
      }
    };

    window.addEventListener('click', handleClickOutside);

    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  // Görünüm modları arasında geçiş
  const handleViewChange = (mode) => {
    setViewMode(mode);
    setIsDropdownOpen(false); // Görünüm değiştiğinde menüyü kapat
  };

  // Görünüme göre render edilecek bileşen
  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <TaskList toggleDropdown={toggleDropdown} isDropdownOpen={isDropdownOpen} dropdownRef={dropdownRef} />;
      case 'board':
        return <BoardView />;
      case 'calendar':
        return <CalendarView />;
      case 'view':
        return <CustomView />;
      default:
        return <TaskList />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => handleViewChange('list')} // List görünümüne geçiş
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => handleViewChange('board')} // Board görünümüne geçiş
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => handleViewChange('calendar')} // Calendar görünümüne geçiş
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => handleViewChange('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Seçilen görünümde görevleri göster */}
      <div className="everything-content">{renderView()}</div>
    </div>
  );
};

export default EverythingView;
