import React, { useState } from 'react';
import './Inbox.css';

const Inbox = () => {
  const [activeTab, setActiveTab] = useState('Inbox');

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="inbox-container">
      <div className="tabs">
        {['Inbox', 'Important', 'Other', 'Snoozed', 'Cleared'].map((tab) => (
          <button
            key={tab}
            className={`tab-button ${activeTab === tab ? 'active' : ''}`}
            onClick={() => handleTabClick(tab)}
          >
            {tab}
          </button>
        ))}
      </div>
      <div className="search-bar">
        <input type="text" placeholder="Search..." />
        <div className="search-icons">
          <span title="Filter">üîç</span>
          <span title="Settings">‚öôÔ∏è</span>
        </div>
      </div>
      <div className="inbox-content">
        {activeTab === 'Inbox' && (
          <div className="inbox-zero">
            <div className="envelope-icon">‚úâÔ∏è</div>
            <h2>Inbox Zero</h2>
            <p>Congratulations! You've read all your important messages.</p>
            <button className="btn">Got it</button>
          </div>
        )}
        {activeTab === 'Important' && (
          <div className="important-content">
            <h2>Important Notifications</h2>
            <p>You have no important notifications.</p>
          </div>
        )}
        {activeTab === 'Other' && (
          <div className="other-content">
            <h2>Other Notifications</h2>
            <p>No other notifications available.</p>
          </div>
        )}
        {activeTab === 'Snoozed' && (
          <div className="snoozed-content">
            <h2>Snoozed Notifications</h2>
            <p>You don't have any snoozed notifications.</p>
          </div>
        )}
        {activeTab === 'Cleared' && (
          <div className="cleared-content">
            <h2>Cleared Notifications</h2>
            <p>All cleared notifications are shown here.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Inbox;
