import React, { useState, useEffect, useRef } from 'react';
import {
  FaHome,
  FaInbox,
  FaComments,
  FaFileAlt,
  FaChartBar,
  FaClock,
  FaEllipsisH,
  FaGlobe,
  FaEye,
  FaPlus,
  FaSearch
} from 'react-icons/fa';

import './Siderbar.css';

const Sidebar = ({ activeTab, setActiveTab }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [menuPosition, setMenuPosition] = useState({ top: 0, left: 0 });
  const menuRef = useRef(null);
  const ellipsisButtonRef = useRef(null);

  // Boşluğa tıklanınca menüyü kapatma işlevi
  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleOutsideClick);
    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, []);

  // Menü pozisyonunu ayarla
  const handleMenuOpen = () => {
    if (ellipsisButtonRef.current) {
      const rect = ellipsisButtonRef.current.getBoundingClientRect();
      setMenuPosition({ top: rect.bottom + window.scrollY, left: rect.left + window.scrollX });
      setMenuOpen(!menuOpen);
    }
  };

  return (
    <div className="sidebar">
      {/* Sidebar navigasyon kısmı */}
      <button
        className={`sidebar-item ${activeTab === 'Home' ? 'active' : ''}`}
        onClick={() => setActiveTab('Home')}
      >
        <FaHome className="icon" /> Home
      </button>
      <button
        className={`sidebar-item ${activeTab === 'Inbox' ? 'active' : ''}`}
        onClick={() => setActiveTab('Inbox')}
      >
        <FaInbox className="icon" /> Inbox
      </button>
      <button
        className={`sidebar-item ${activeTab === 'Chat' ? 'active' : ''}`}
        onClick={() => setActiveTab('Chat')}
      >
        <FaComments className="icon" /> Chat
      </button>
      <button
        className={`sidebar-item ${activeTab === 'Docs' ? 'active' : ''}`}
        onClick={() => setActiveTab('Docs')}
      >
        <FaFileAlt className="icon" /> Docs
      </button>
      <button
        className={`sidebar-item ${activeTab === 'Dashboards' ? 'active' : ''}`}
        onClick={() => setActiveTab('Dashboards')}
      >
        <FaChartBar className="icon" /> Dashboards
      </button>
      <button
        className={`sidebar-item ${activeTab === 'Timesheets' ? 'active' : ''}`}
        onClick={() => setActiveTab('Timesheets')}
      >
        <FaClock className="icon" /> Timesheets
      </button>
      <button
        className={`sidebar-item ${activeTab === 'More' ? 'active' : ''}`}
        onClick={() => setActiveTab('More')}
      >
        <FaEllipsisH className="icon" ref={ellipsisButtonRef} /> More
      </button>

      {/* Spaces bölüm başlığı ve ikon butonları */}
      <div className="spaces">
        <div className="spaces-header">
          <h3 className="spaces-title">Spaces</h3>
          <div className="icon-button-group">
            <button className="icon-button small-icon-button" title="More options" onClick={handleMenuOpen}>
              <FaEllipsisH className="icon" />
            </button>
            <button className="icon-button small-icon-button" title="Search">
              <FaSearch className="icon" />
            </button>
            <button className="icon-button small-icon-button create-space-button" title="Create new space">
              <FaPlus className="icon" />
            </button>
          </div>
        </div>

        {/* Açılır menü */}
        {menuOpen && (
          <div
            className="dropdown-menu"
            ref={menuRef}
            style={{
              position: 'absolute',
              top: `${menuPosition.top}px`,
              left: `${menuPosition.left}px`,
            }}
          >
            <ul>
              <li><FaPlus className="icon" /> Create Space</li>
              <li><FaEye className="icon" /> Manage Spaces</li>
              <hr />
              <li><FaEye className="icon" /> Show all Spaces</li>
              <li><FaEye className="icon" /> Show archived</li>
            </ul>
          </div>
        )}

        {/* Spaces içerik */}
        <div className="spaces">
          <button
            className={`space-item ${activeTab === 'Everything' ? 'active' : ''}`}
            onClick={() => setActiveTab('Everything')}
          >
            <FaGlobe className="icon" /> Everything
          </button>
          <button className="space-item">
            <FaEye className="icon" /> View All Spaces
          </button>
          <button className="space-item">
            <FaPlus className="icon" /> Create Space
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
