import React, { useState } from 'react';
import './CreateSpaceModal.css';

const CreateSpaceModal = ({ onClose }) => {
  const [spaceName, setSpaceName] = useState('');
  const [description, setDescription] = useState('');
  const [isPrivate, setIsPrivate] = useState(false);

  const handleCreate = () => {
    if (spaceName.trim() === '') {
      alert('Space name is required');
      return;
    }
    // Alanları backend'e gönderme veya işlemler
    console.log({
      spaceName,
      description,
      isPrivate,
    });
    onClose(); // Modal kapatma
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Create a Space</h2>
          <button className="close-button" onClick={onClose}>×</button> {/* Kapatma butonu */}
        </div>
        <p className="modal-description">
          A space represents teams, departments, or groups, each with its own Lists, workflows, and settings.
        </p>
        
        <form className="space-form">
          <label>Icon & Name</label>
          <input
            type="text"
            placeholder="e.g. Marketing, Engineering, HR"
            value={spaceName}
            onChange={(e) => setSpaceName(e.target.value)}
            required
          />
          
          <label>Description (optional)</label>
          <textarea
            placeholder="Description (optional)"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="3"
          ></textarea>
          
          <div className="make-private">
            <label>
              <input
                type="checkbox"
                checked={isPrivate}
                onChange={() => setIsPrivate(!isPrivate)}
              /> 
              Make Private
            </label>
          </div>
          
          <div className="modal-actions">
            <button type="button" className="modal-button" onClick={onClose}>Cancel</button>
            <button type="button" className="modal-button-primary" onClick={handleCreate}>Continue</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateSpaceModal;
