// src/components/EverythingView/EverythingView.js
import React, { useState } from 'react';
import TaskList from './TaskList';
import './EverythingView.css';

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list');

  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <TaskList />;
      case 'board':
        return <div>Board View</div>;
      case 'calendar':
        return <div>Calendar View</div>;
      case 'view':
        return <div>+View</div>;
      default:
        return <TaskList />;
    }
  };

  return (
    <div className="everything-container">
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          <button className="view-btn" onClick={() => setViewMode('list')}>
            List
          </button>
          <button className="view-btn" onClick={() => setViewMode('board')}>
            Board
          </button>
          <button className="view-btn" onClick={() => setViewMode('calendar')}>
            Calendar
          </button>
          <button className="view-btn" onClick={() => setViewMode('view')}>
            +View
          </button>
        </div>
      </div>
      <div className="everything-content">{renderView()}</div>
    </div>
  );
};

export default EverythingView;
