import React, { useState, useEffect, useRef } from 'react';
import {
  FaList, FaTh, FaCalendarAlt, FaPlus, FaLayerGroup, FaFilter, FaSort, FaEye
} from 'react-icons/fa'; 
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView'; 
import CreateTaskModal from '../Modal/CreateTaskModal';
import './EverythingView.css'; 

const EverythingView = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [viewMode, setViewMode] = useState('list'); 
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); 
  const dropdownRef = useRef(null); 

  const toggleDropdown = (e) => {
    e.stopPropagation(); 
    if (viewMode === 'list') { // Sadece list modunda menü açılacak
      setIsDropdownOpen(!isDropdownOpen);
    }
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    window.addEventListener('click', handleClickOutside);

    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return (
          <div>
            <TaskList tasks={filteredTasks} />
            {isDropdownOpen && (
              <div className="dropdown-menu" ref={dropdownRef}>
                <button>Rename</button>
                <button>Copy link to view</button>
                <button>Add to favorites</button>
                <button>Customize view</button>
                <button>Pin view</button>
                <button>Autosave for me</button>
                <button>Delete view</button>
                <button>Sharing & Permissions</button>
              </div>
            )}
          </div>
        );
      case 'board':
        return <BoardView tasks={filteredTasks} />;
      case 'calendar':
        return <CalendarView tasks={filteredTasks} />;
      case 'view':
        return <CustomView />; 
      default:
        return <TaskList tasks={filteredTasks} />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={toggleDropdown} // List butonuna tıklandığında menü açılacak
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')} // Board kısmına geçiş
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')} 
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => setViewMode('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Menü Düzenlemeleri */}
      {(viewMode === 'list' || viewMode === 'board') && (
        <div className="filter-buttons">
          <button className="filter-btn"><FaLayerGroup /> Group: Status</button>
          <button className="filter-btn">Subtasks: Collapse all</button>
          <button className="filter-btn"><FaFilter /> Filters</button>
          <button className="filter-btn"><FaSort /> {viewMode === 'board' ? 'Columns' : 'Sort'}</button>
          <button className="filter-btn"><FaEye /> Show closed</button>
          <button className="filter-btn">Hide</button>
        </div>
      )}

      {/* Board görünümünde üst menüyü kaldırıyoruz */}
      {viewMode === 'board' && (
        <div className="task-controls">
          <input
            type="text"
            placeholder="Search tasks..."
            className="search-bar"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          />
          <button className="add-task-btn" onClick={() => setIsModalOpen(true)}>
            <FaPlus /> Add Task
          </button>
        </div>
      )}

      {/* Seçilen görünümde görevleri göster */}
      <div className="everything-content">{renderView()}</div>

      {/* Görev oluşturma modalı */}
      {isModalOpen && (
        <CreateTaskModal onClose={() => setIsModalOpen(false)} onCreate={(newTask) => setTasks([...tasks, newTask])} />
      )}
    </div>
  );
};

export default EverythingView;
