import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaPlus,
} from 'react-icons/fa';
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView';
import ListView from './ListView'; // ListView'yi ekledik
import './EverythingView.css';

const EverythingView = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları (list, board, calendar, custom view)
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal açma/kapatma kontrolü
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Dropdown menü kontrolü
  const dropdownRef = useRef(null); // Dropdown menüsü için referans

  const userInfo = JSON.parse(localStorage.getItem('userInfo'));

  // API'den görevleri çekme
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        if (!userInfo || !userInfo.token) {
          console.error('No valid user info or token found in localStorage');
          return;
        }

        const config = {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        };

        const { data } = await axios.get('/api/tasks', config);

        if (!data) {
          throw new Error('API returned undefined data');
        }

        setTasks(data);
      } catch (error) {
        console.error(
          'Error fetching tasks:',
          error.message || error.response?.data?.message || 'Unknown error'
        );
      }
    };

    fetchTasks();
  }, [userInfo]);

  // Yeni görev eklendiğinde state'i güncelleme
  const handleCreateTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  // Dropdown menüyü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation();
    if (viewMode === 'list') {
      setIsDropdownOpen(!isDropdownOpen); // Sadece list modunda açılacak
    }
  };

  // Boşluğa tıklayınca menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    window.addEventListener('click', handleClickOutside);

    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  // Filtreleme işlemi
  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  // View moduna göre ilgili komponenti render etme
  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <ListView tasks={filteredTasks} handleCreateTask={handleCreateTask} filter={filter} setFilter={setFilter} />;
      case 'board':
        return <BoardView tasks={filteredTasks} />;
      case 'calendar':
        return <CalendarView tasks={filteredTasks} />;
      case 'view':
        return <CustomView />;
      default:
        return <TaskList tasks={filteredTasks} />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={toggleDropdown} // Dropdown menüyü açmak için tıklama
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')}
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')}
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => setViewMode('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Dropdown menü sadece List görünümünde açılacak */}
      {viewMode === 'list' && isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <ul>
            <li>Rename</li>
            <li>Copy link to view</li>
            <li>Add to favorites</li>
            <li>Customize view</li>
            <li>Pin view</li>
            <li>Autosave for me</li>
            <li>Delete view</li>
            <li>Sharing & Permissions</li>
          </ul>
        </div>
      )}

      {/* Seçilen görünümde görevleri göster */}
      <div className="everything-content">{renderView()}</div>
    </div>
  );
};

export default EverythingView;
