import React, { useState } from 'react';
import './AssignedToMe.css';
import { FaCog, FaExpand, FaEllipsisH, FaTrash } from 'react-icons/fa';

const AssignedToMe = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [showSettings, setShowSettings] = useState(false);

  const handleModalOpen = () => {
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  const removeCard = () => {
    alert("Card removed!");
  };

  return (
    <div className="assigned-to-me card">
      <div className="header">
        <h2>Assigned to me</h2>
        <div className="actions">
          <button className="icon-btn" title="Settings">
            <FaCog />
          </button>
          <button className="icon-btn" title="Full screen">
            <FaExpand />
          </button>
          <button className="icon-btn" title="More settings" onClick={toggleSettings}>
            <FaEllipsisH />
          </button>
        </div>
      </div>

      {showSettings && (
        <div className="more-settings-menu">
          <button className="remove-button" onClick={removeCard}>
            <FaTrash className="trash-icon" /> Remove card
          </button>
        </div>
      )}

      <div className="empty-state">
        <span role="img" aria-label="check" className="checkmark">✔️</span>
        <p>Tasks assigned to you will appear here. <a href="#">Learn more</a></p>
        <button className="add-task-button" onClick={handleModalOpen}>+ Add task</button>
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Add Task</h2>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssignedToMe;
