import React, { useState } from 'react';
import './AssignedToMe.css'; // CSS dosyasƒ± gerektiƒüinde

const AssignedToMe = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [showSettings, setShowSettings] = useState(false);

  // Task ekleme modalƒ±nƒ± a√ßma ve kapama
  const handleModalOpen = () => {
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  // ... d√ºƒümesine tƒ±klanƒ±nca ayarlarƒ± a√ßma/kapama
  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  const removeCard = () => {
    alert("Card removed!");
  };

  return (
    <div className="assigned-to-me card">
      <div className="header">
        <h2>Assigned to me</h2>
        <div className="actions">
          <button className="icon-btn" title="Add task" onClick={handleModalOpen}>
            <span role="img" aria-label="add-task">‚ûï</span>
          </button>
          <button className="icon-btn" title="Expand" onClick={() => console.log('Expand')}>
            ‚õ∂
          </button>
          <button className="icon-btn" title="More settings" onClick={toggleSettings}>
            ‚ãÆ
          </button>
          {showSettings && (
            <div className="more-settings-menu">
              <button className="remove-button" onClick={removeCard}>üóëÔ∏è Remove card</button>
            </div>
          )}
        </div>
      </div>

      <div className="empty-state">
        <span role="img" aria-label="check" className="checkmark">‚úîÔ∏è</span>
        <p>Tasks assigned to you will appear here. <a href="#">Learn more</a></p>
        <button className="add-task-button" onClick={handleModalOpen}>+ Add task</button>
      </div>

      {/* Eƒüer modal a√ßƒ±ksa task ekleme modali g√∂r√ºn√ºr */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>Add Task</h2>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssignedToMe;
