import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EverythingView.css';
import CreateTask from '../CreateTask';

const EverythingView = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [viewMode, setViewMode] = useState('list'); // Arayüz için mod

  const userInfo = JSON.parse(localStorage.getItem('userInfo'));

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        if (!userInfo || !userInfo.token) {
          console.error('No valid user info or token found in localStorage');
          return; 
        }

        const config = {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        };

        const { data } = await axios.get('/api/tasks', config);

        if (!data) {
          throw new Error('API returned undefined data');
        }

        setTasks(data);
      } catch (error) {
        console.error('Error fetching tasks:', error.message || error.response?.data?.message || 'Unknown error');
      }
    };

    fetchTasks();
  }, [userInfo]);

  // Yeni görev eklendiğinde state'i güncelle
  const handleTaskCreated = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  // Filtreleme işlemi
  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  // Görünüm modu değişimi
  const handleViewChange = (mode) => {
    setViewMode(mode);
  };

  return (
    <div className="everything-container">
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="everything-toolbar">
          <button
            className={`toolbar-button ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => handleViewChange('list')}
          >
            List
          </button>
          <button
            className={`toolbar-button ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => handleViewChange('board')}
          >
            Board
          </button>
          <button
            className={`toolbar-button ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => handleViewChange('calendar')}
          >
            Calendar
          </button>
          <input
            type="text"
            className="search-bar"
            placeholder="Search tasks..."
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          />
        </div>
      </div>

      {/* Görev oluşturma formu */}
      <CreateTask onTaskCreated={handleTaskCreated} />

      <div className="everything-content">
        {filteredTasks.length > 0 ? (
          filteredTasks.map((task) => (
            <div key={task._id} className="task-item">
              <h3>{task.title}</h3>
              <p>Status: {task.status}</p>
              <p>Due Date: {task.dueDate}</p>
            </div>
          ))
        ) : (
          <p>No tasks found.</p>
        )}
      </div>
    </div>
  );
};

export default EverythingView;
