import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import {
  FaHome,
  FaInbox,
  FaComments,
  FaFileAlt,
  FaChartBar,
  FaClock,
  FaEllipsisH,
  FaGlobe,
  FaEye,
  FaPlus,
} from 'react-icons/fa';

import Home from './components/Home';
import Inbox from './components/Inbox';
import Chat from './components/Chat';
import Docs from './components/Docs';
import Dashboards from './components/Dashboards';
import Timesheets from './components/Timesheets';
import More from './components/More';
import EverythingPage from './pages/EverythingPage';
import ChatContent from './components/ChatContent'; // ChatContent bileşeni

const App = () => {
  const [activeTab, setActiveTab] = useState('Home');
  const [isMenuOpen, setIsMenuOpen] = useState(false); // Menü durumu
  const menuRef = useRef(null);

  // Sayfanın herhangi bir yerine tıklanınca menüyü kapat
  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleOutsideClick);
    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, []);

  const renderContent = () => {
    switch (activeTab) {
      case 'Home':
        return <Home />;
      case 'Inbox':
        return <Inbox />;
      case 'Chat':
        return <Chat />;
      case 'Docs':
        return <Docs />;
      case 'Dashboards':
        return <Dashboards />;
      case 'Timesheets':
        return <Timesheets />;
      case 'More':
        return <More />;
      case 'Everything':
        return <EverythingPage />;
      default:
        return <Home />;
    }
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="container">
      <>
        {/* Sidebar bileşeni */}
        <div className="sidebar">
          <button
            className={`sidebar-item ${activeTab === 'Home' ? 'active' : ''}`}
            onClick={() => handleTabClick('Home')}
          >
            <FaHome className="icon" /> Home
          </button>
          <button
            className={`sidebar-item ${activeTab === 'Inbox' ? 'active' : ''}`}
            onClick={() => handleTabClick('Inbox')}
          >
            <FaInbox className="icon" /> Inbox
          </button>
          <button
            className={`sidebar-item ${activeTab === 'Chat' ? 'active' : ''}`}
            onClick={() => handleTabClick('Chat')}
          >
            <FaComments className="icon" /> Chat
          </button>
          <button
            className={`sidebar-item ${activeTab === 'Docs' ? 'active' : ''}`}
            onClick={() => handleTabClick('Docs')}
          >
            <FaFileAlt className="icon" /> Docs
          </button>
          <button
            className={`sidebar-item ${activeTab === 'Dashboards' ? 'active' : ''}`}
            onClick={() => handleTabClick('Dashboards')}
          >
            <FaChartBar className="icon" /> Dashboards
          </button>
          <button
            className={`sidebar-item ${activeTab === 'Timesheets' ? 'active' : ''}`}
            onClick={() => handleTabClick('Timesheets')}
          >
            <FaClock className="icon" /> Timesheets
          </button>
          <button
            className={`sidebar-item ${activeTab === 'More' ? 'active' : ''}`}
            onClick={() => handleTabClick('More')}
          >
            <FaEllipsisH className="icon" /> More
          </button>
          <div className="spaces">
            <h3>Spaces</h3>
            <button
              className={`space-item ${activeTab === 'Everything' ? 'active' : ''}`}
              onClick={() => handleTabClick('Everything')}
            >
              <FaGlobe className="icon" /> Everything
            </button>
            <button className="space-item">
              <FaEye className="icon" /> View All Spaces
            </button>
            <button className="space-item">
              <FaPlus className="icon" /> Create Space
            </button>
          </div>
        </div>

        {/* Main Content ve ChatContent */}
        <div className="main-content">
          <div className="top-bar">
            <input type="text" placeholder="Search..." className="search-bar" />
            <div className="top-bar-icons">
              <span title="Notifications">🔔</span>
              <span title="Profile">👤</span>
            </div>
          </div>
          <div className="content">
            {renderContent()}
            <ChatContent /> {/* ChatContent bileşeni */}
          </div>
        </div>
      </>
    </div>
  );
};

export default App;
