import React, { useState } from 'react';
import './App.css';
import { FaHome, FaInbox, FaComments, FaFileAlt, FaChartBar, FaClock, FaEllipsisH, FaGlobe, FaEye, FaPlus } from 'react-icons/fa';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom'; // Router import edildi
import HomeContent from './components/HomeContent';

const AppContent = () => {
  const [activeTab, setActiveTab] = useState('Home');
  const location = useLocation(); // useLocation, AppContent iÃ§inde

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const isHomeRoute = location.pathname === '/'; // Home route'unda olup olmadÄ±ÄŸÄ±nÄ± kontrol ediyoruz

  return (
    <div className="container">
      {isHomeRoute && ( // YalnÄ±zca Home route'unda sidebar ve Ã¼st barÄ± gÃ¶ster
        <>
          <div className="sidebar">
            <button className={`sidebar-item ${activeTab === 'Home' ? 'active' : ''}`} onClick={() => handleTabClick('Home')}>
              <FaHome className="icon" /> Home
            </button>
            <button className={`sidebar-item ${activeTab === 'Inbox' ? 'active' : ''}`} onClick={() => handleTabClick('Inbox')}>
              <FaInbox className="icon" /> Inbox
            </button>
            <button className={`sidebar-item ${activeTab === 'Chat' ? 'active' : ''}`} onClick={() => handleTabClick('Chat')}>
              <FaComments className="icon" /> Chat
            </button>
            <button className={`sidebar-item ${activeTab === 'Docs' ? 'active' : ''}`} onClick={() => handleTabClick('Docs')}>
              <FaFileAlt className="icon" /> Docs
            </button>
            <button className={`sidebar-item ${activeTab === 'Dashboards' ? 'active' : ''}`} onClick={() => handleTabClick('Dashboards')}>
              <FaChartBar className="icon" /> Dashboards
            </button>
            <button className={`sidebar-item ${activeTab === 'Timesheets' ? 'active' : ''}`} onClick={() => handleTabClick('Timesheets')}>
              <FaClock className="icon" /> Timesheets
            </button>
            <button className={`sidebar-item ${activeTab === 'More' ? 'active' : ''}`} onClick={() => handleTabClick('More')}>
              <FaEllipsisH className="icon" /> More
            </button>
            <div className="favorites">
              <h3>Favorites</h3>
            </div>
            <div className="spaces">
              <h3>Spaces</h3>
              <button className="space-item">
                <FaGlobe className="icon" /> Everything
              </button>
              <button className="space-item">
                <FaEye className="icon" /> View All Spaces
              </button>
              <button className="space-item">
                <FaPlus className="icon" /> Create Space
              </button>
            </div>
          </div>
          <div className="top-bar">
            <input type="text" placeholder="Search..." className="search-bar" />
            <div className="top-bar-icons">
              <span title="Notifications">ðŸ””</span>
              <span title="Profile">ðŸ‘¤</span>
            </div>
          </div>
        </>
      )}
      <div className="main-content">
        <div className="content">
          <Routes>
            <Route path="/" element={<HomeContent />} />
            {/* DiÄŸer sayfalar buraya eklenebilir */}
          </Routes>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router> {/* Router bileÅŸeni App'in etrafÄ±nda */}
      <AppContent />
    </Router>
  );
};

export default App;
