import React, { useState, useEffect, useRef } from 'react';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaPlus,
  FaLink,
  FaStar,
  FaEdit,
  FaLock,
  FaSave,
  FaTrashAlt,
  FaShareAlt,
  FaLayerGroup,
  FaFilter,
  FaColumns,
  FaEye,
} from 'react-icons/fa';
import ListView from './ListView'; 
import BoardView from './BoardView'; 
import CalendarView from './CalendarView'; 
import CustomView from './CustomView'; // Özel görünüm
import CreateTaskModal from '../Modal/CreateTaskModal';
import './EverythingView.css'; 

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları (list, board, calendar, custom view)
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal açma/kapatma kontrolü
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // List menü açma kontrolü
  const dropdownRef = useRef(null); // Dropdown menüsü için referans

  // Dropdown menüyü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation();
    if (viewMode === 'list') {
      setIsDropdownOpen(!isDropdownOpen); // Sadece list modunda açılacak
    }
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    window.addEventListener('click', handleClickOutside);
    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  // Görünüm modlarına göre render edilen component
  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <ListView />;
      case 'board':
        return <BoardView />;
      case 'calendar':
        return <CalendarView />;
      case 'view':
        return <CustomView />;
      default:
        return <ListView />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={toggleDropdown}
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')}
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')}
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button
            className="view-btn"
            onClick={() => setViewMode('view')}
          >
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Dropdown menü sadece List görünümünde açılacak */}
      {viewMode === 'list' && isDropdownOpen && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <ul>
            <li><FaEdit /> Rename</li>
            <li><FaLink /> Copy link to view</li>
            <li><FaStar /> Add to favorites</li>
            <li><FaEdit /> Customize view</li>
            <div className="toggle">
              <span><FaLock /> Pin view</span>
              <input type="checkbox" />
            </div>
            <div className="toggle">
              <span><FaLock /> Private view</span>
              <input type="checkbox" />
            </div>
            <div className="toggle">
              <span><FaSave /> Autosave for me</span>
              <input type="checkbox" />
            </div>
            <div className="toggle">
              <span>Set as default view</span>
              <input type="checkbox" />
            </div>
            <li><FaTrashAlt /> Delete view</li>
            <li><FaShareAlt /> Sharing & Permissions</li>
          </ul>
        </div>
      )}

      {/* Filtre Butonları */}
      <div className="filter-buttons">
        <button className="filter-btn"><FaLayerGroup /> Group: Status</button>
        <button className="filter-btn">Subtasks: Collapse all</button>
        {viewMode === 'board' ? (
          <button className="filter-btn"><FaColumns /> Columns</button>
        ) : (
          <button className="filter-btn"><FaSort /> Sort</button>
        )}
        <button className="filter-btn"><FaEye /> Show closed</button>
        <button className="filter-btn">Hide</button>
      </div>

      {/* Seçilen görünümde görevleri göster */}
      <div className="everything-content">
        {renderView()}
      </div>

      {/* Görev oluşturma modalı */}
      {isModalOpen && (
        <CreateTaskModal onClose={() => setIsModalOpen(false)} />
      )}
    </div>
  );
};

export default EverythingView;
