import React, { useState, useRef, useEffect } from 'react';
import './Home.css';
import Recents from './Recents';
import Agenda from './Agenda';
import MyWork from './MyWork';
import AssignedToMe from './AssignedToMe';

const Home = () => {
  const [isTaskFormOpen, setIsTaskFormOpen] = useState(false);
  const formRef = useRef(null);

  // Sayfa dışına tıklayınca formu kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (formRef.current && !formRef.current.contains(event.target)) {
        setIsTaskFormOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="home-page">
      <h1>Good evening, Kaan</h1>
      <div className="grid-container">
        <Recents />
        <Agenda />
        <MyWork />
        <AssignedToMe />

        <button onClick={() => setIsTaskFormOpen(!isTaskFormOpen)}>
          Add Task
        </button>

        {isTaskFormOpen && (
          <div className="task-form-container" ref={formRef}>
            <h3>Add Task</h3>
            <form>
              <label>Task Name:</label>
              <input type="text" placeholder="Enter task name" />

              <label>Description:</label>
              <textarea placeholder="Add description"></textarea>

              <label>Due Date:</label>
              <input type="date" />

              <label>Priority:</label>
              <select>
                <option>Low</option>
                <option>Medium</option>
                <option>High</option>
              </select>

              <div className="form-buttons">
                <button type="button" onClick={() => setIsTaskFormOpen(false)}>Close</button>
                <button type="submit">Create Task</button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
