import React, { useState } from 'react';
import './Recents.css';

const Recents = () => {
  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedProjectIndex, setSelectedProjectIndex] = useState(null);

  const handleAddProject = () => {
    if (newProject.trim() !== "") {
      setProjects([...projects, { name: newProject, dashboard: false }]);
      setNewProject("");
    }
  };

  const handleDeleteProject = (index) => {
    const updatedProjects = projects.filter((_, i) => i !== index);
    setProjects(updatedProjects);
  };

  const handleRenameProject = (index) => {
    const newName = prompt("Enter the new project name:");
    if (newName && newName.trim() !== "") {
      const updatedProjects = projects.map((project, i) =>
        i === index ? { ...project, name: newName } : project
      );
      setProjects(updatedProjects);
    }
  };

  const toggleDropdown = (index) => {
    setSelectedProjectIndex(index);
    setShowDropdown(!showDropdown);
  };

  const handleAddDashboard = (index) => {
    const updatedProjects = projects.map((project, i) =>
      i === index ? { ...project, dashboard: true } : project
    );
    setProjects(updatedProjects);
  };

  return (
    <div className="recents card">
      <div className="header">
        <h2>Recents</h2>
      </div>
      <div className="project-input">
        {projects.length === 0 ? (
          <p>No recent projects available.</p>
        ) : (
          projects.map((project, index) => (
            <div key={index} className="project-item">
              <span>{project.name} • in Project Notes</span>
              <div className="project-actions">
                <button
                  className="icon-button"
                  title="Open in new tab"
                  onClick={() => window.open("#", "_blank")}
                >
                  🔗
                </button>
                <button
                  className="icon-button"
                  title="Copy link"
                  onClick={() => navigator.clipboard.writeText(project.name)}
                >
                  📋
                </button>
                <button
                  className="icon-button"
                  title="Rename project"
                  onClick={() => handleRenameProject(index)}
                >
                  ✏️
                </button>
                <button
                  className="icon-button"
                  title="Delete project"
                  onClick={() => handleDeleteProject(index)}
                >
                  🗑️
                </button>
              </div>
              {project.dashboard && (
                <div className="dashboard">
                  <span>Dashboard</span>
                  <div className="dashboard-actions">
                    <button
                      className="icon-button"
                      title="Open in new tab"
                      onClick={() => window.open("#", "_blank")}
                    >
                      🔗
                    </button>
                    <button
                      className="icon-button"
                      title="Copy link"
                      onClick={() => navigator.clipboard.writeText("Dashboard link")}
                    >
                      📋
                    </button>
                  </div>
                </div>
              )}
            </div>
          ))
        )}
        <div className="add-project">
          <input
            type="text"
            value={newProject}
            placeholder="Enter project name"
            onChange={(e) => setNewProject(e.target.value)}
          />
          <button className="create-project" onClick={handleAddProject}>
            + Create New Project
          </button>
        </div>
      </div>
    </div>
  );
};

export default Recents;
