import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaPlus,
  FaLink,
  FaStar,
  FaEdit,
  FaLock,
  FaSave,
  FaTrashAlt,
  FaShareAlt,
  FaLayerGroup,
  FaFilter,
  FaSort,
  FaEye,
} from 'react-icons/fa'; // İkonlar
import TaskList from './TaskList';
import BoardView from './BoardView';
import CalendarView from './CalendarView';
import CustomView from './CustomView'; // Yeni görünüm eklemek için
import CreateTaskModal from '../Modal/CreateTaskModal';
import './EverythingView.css'; // CSS dosyası

const EverythingView = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [viewMode, setViewMode] = useState('list'); // Görünüm modları (list, board, calendar, custom view)
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal açma/kapatma kontrolü
  const [isListMenuOpen, setIsListMenuOpen] = useState(false); // List menüsü açma/kapatma kontrolü
  const dropdownRef = useRef(null); // Dropdown menüsü için referans oluşturma

  // Dropdown menüyü açma/kapatma
  const toggleListMenu = (e) => {
    e.stopPropagation(); // Menü dışında başka yere tıklandığında menü kapanmasın
    setIsListMenuOpen(!isListMenuOpen);
  };

  // Boş bir alana tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsListMenuOpen(false);
      }
    };

    // Pencereye tıklama olayını ekle
    window.addEventListener('click', handleClickOutside);
    
    // Bileşen temizlendiğinde olay dinleyicisini kaldır
    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const userInfo = JSON.parse(localStorage.getItem('userInfo'));

  // API'den görevleri çekme
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        if (!userInfo || !userInfo.token) {
          console.error('No valid user info or token found in localStorage');
          return;
        }

        const config = {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        };

        const { data } = await axios.get('/api/tasks', config);

        if (!data) {
          throw new Error('API returned undefined data');
        }

        setTasks(data);
      } catch (error) {
        console.error(
          'Error fetching tasks:',
          error.message || error.response?.data?.message || 'Unknown error'
        );
      }
    };

    fetchTasks();
  }, [userInfo]);

  // Yeni görev eklendiğinde state'i güncelleme
  const handleCreateTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  // Filtreleme işlemi
  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(filter.toLowerCase())
  );

  // Görünüm moduna göre ilgili komponenti render etme
  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return (
          <div>
            {/* List menüsü */}
            {isListMenuOpen && (
              <div className="dropdown-menu" ref={dropdownRef}>
                <button><FaEdit /> Rename</button>
                <button><FaLink /> Copy link to view</button>
                <button><FaStar /> Add to favorites</button>
                <button><FaEdit /> Customize view</button>
                <div className="toggle">
                  <span><FaLock /> Pin view</span>
                  <input type="checkbox" />
                </div>
                <div className="toggle">
                  <span><FaLock /> Private view</span>
                  <input type="checkbox" />
                </div>
                <div className="toggle">
                  <span><FaSave /> Autosave for me</span>
                  <input type="checkbox" />
                </div>
                <div className="toggle">
                  <span>Set as default view</span>
                  <input type="checkbox" />
                </div>
                <div className="submenu">
                  <button><FaSave /> Export view</button>
                  <button><FaEdit /> Duplicate view</button>
                </div>
                <button><FaTrashAlt /> Delete view</button>
                <button><FaShareAlt /> Sharing & Permissions</button>
              </div>
            )}

            {/* Görev listesi görünümü */}
            <div className="filter-buttons">
              <button className="filter-btn"><FaLayerGroup /> Group: Status</button>
              <button className="filter-btn">Subtasks: Collapse all</button>
              <button className="filter-btn"><FaFilter /> Filters</button>
              <button className="filter-btn"><FaSort /> Sort</button>
              <button className="filter-btn"><FaEye /> Show closed</button>
              <button className="filter-btn">Hide</button>
            </div>

            {/* Görev listesi */}
            <TaskList tasks={filteredTasks} />
          </div>
        );
      case 'board':
        return <BoardView tasks={filteredTasks} />;
      case 'calendar':
        return <CalendarView tasks={filteredTasks} />;
      case 'view':
        return <CustomView />; // Yeni görünüm ekleme sayfası
      default:
        return <TaskList tasks={filteredTasks} />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <h1>Everything</h1>
        <div className="view-options">
          {/* List butonu: Menü ve görünüm arasında geçiş yapar */}
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={() => {
              if (viewMode === 'list') {
                toggleListMenu();
              } else {
                setViewMode('list');
              }
            }}
          >
            <FaList /> List
          </button>

          {/* Diğer görünüm butonları */}
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')}
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')}
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn" onClick={() => setViewMode('view')}>
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* Görünüm render */}
      <div className="everything-content">{renderView()}</div>

      {/* Görev oluşturma modalı */}
      {isModalOpen && (
        <CreateTaskModal onClose={() => setIsModalOpen(false)} onCreate={handleCreateTask} />
      )}
    </div>
  );
};

export default EverythingView;
