import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import {
  FaHome,
  FaInbox,
  FaComments,
  FaFileAlt,
  FaChartBar,
  FaClock,
  FaEllipsisH,
  FaGlobe,
  FaEye,
  FaPlus,
} from 'react-icons/fa';
import Sidebar from './Sidebar';
import ChatContent from './ChatContent';
import Home from './components/Home';
import Inbox from './components/Inbox';
import Chat from './components/Chat';
import Docs from './components/Docs';
import Dashboards from './components/Dashboards';
import Timesheets from './components/Timesheets';
import More from './components/More';
import EverythingPage from './pages/EverythingPage';

const App = () => {
  const [activeTab, setActiveTab] = useState('Home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  // SayfanÄ±n herhangi bir yerine tÄ±klanÄ±nca menÃ¼yÃ¼ kapat
  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleOutsideClick);
    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, []);

  const renderContent = () => {
    switch (activeTab) {
      case 'Home':
        return <Home />;
      case 'Inbox':
        return <Inbox />;
      case 'Chat':
        return <Chat />;
      case 'Docs':
        return <Docs />;
      case 'Dashboards':
        return <Dashboards />;
      case 'Timesheets':
        return <Timesheets />;
      case 'More':
        return <More />;
      case 'Everything':
        return <EverythingPage />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="app-container">
      {/* Sidebar BileÅŸeni */}
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />

      {/* Main Content ve ChatContent */}
      <div className="main-content">
        <div className="top-bar">
          <input type="text" placeholder="Search..." className="search-bar" />
          <div className="top-bar-icons">
            <span title="Notifications">ðŸ””</span>
            <span title="Profile">ðŸ‘¤</span>
          </div>
        </div>
        <div className="content">
          {renderContent()}
          {activeTab === 'Chat' && <ChatContent />} {/* Sadece Chat sekmesinde ChatContent gÃ¶ster */}
        </div>
      </div>
    </div>
  );
};

export default App;
